mapper.update:
update board
set b_count = b_count + 1
where b_num = #{b_num}

mapper.select:
select b_changed_name
from board_image
where b_num = #{b_num}

mapper.update:
update board
set b_status = 'N'
where b_num = #{b_num}

update board
set b_title = #{b_title}, b_content = #{b_content}, b_subcategory = #{b_subcategory}
where b_num = #{b_num}

mapper.select:
select count(*)
from board
where b_status = 'Y'
	and b_category = #{b_category}

	and m_id = #{m_id}

select *
from board
where b_status = 'Y'
	and b_category = #{b_category}

	and m_id = #{m_id}

order by B_REG_DATE desc
