mapper.select:
select count(*)
from member where
m_id = #{id}

select count(*)
from member where
m_nickname = #{m_nickname}

select * 
from member
where m_name = #{m_name} and m_status='Y'

	and m_phone = #{m_phone}


	and m_email = #{m_email}

mapper.update:
update member
set m_pw = #{m_pw}
where m_id = #{m_id} and m_status = 'Y'

mapper.select:

select count(*)
from LEC_NOPAYVIEW
where M_ID = #{m_id}
